function App() {
  const days = new Date().getTime();
  const birthday = new Date(2022, 4, 5).getTime();
  const daysUntill = (birthday - days) / (1000 * 60 * 60 * 24);
  const remainingDays = daysUntill.toFixed(0);
  const hoursUntill = ((daysUntill * 24) % 24).toFixed(0);
  const minutesUntill = ((daysUntill * (24 * 60)) % 60).toFixed(0);
  let secondsUntill = ((daysUntill * (24 * 60 * 60)) % 60).toFixed(0);

  // const days = new Date().getTime();
  // const days = days / (1000 * 60 * 60);

  // const birthday = new Date(2022, 4, 5).getTime() / (1000 * 60 * 60);
  // const daysUntill = ((birthday - daysSince) / 24).toFixed(0);
  // // const hoursUntill = ((birthday - daysSince) % 24).toFixed(0);
  // // const minutesUntill = ((birthday - daysSince) * 60) % 60;

  setTimeout(() => {
    secondsUntill--;
  }, 1000);

  return (
    <div>
      <h2>{remainingDays}</h2>
      <h2>{hoursUntill}</h2>
      <h2>{minutesUntill}</h2>
      <h2>{secondsUntill}</h2>

      <h3>{`You have: ${remainingDays}Days: ${hoursUntill}Hours:${minutesUntill} Minutes: ${secondsUntill}Seconds, To Your Birth Day`}</h3>
    </div>
  );
}

export default App;
