import { useEffect, useState } from "react";

function App() {
  // const remainingDays = daysUntill.toFixed(0);
  // const hoursUntill = ((daysUntill * 24) % 24).toFixed(0);
  // const minutesUntill = ((daysUntill * (24 * 60)) % 60).toFixed(0);
  // let secondsUntill = ((daysUntill * (24 * 60 * 60)) % 60).toFixed(0);

  const [remainingDays, setRemainingDays] = useState("00");
  const [hoursUntill, setHoursUntil] = useState("00");
  const [minutesUntil, setMinutesUntil] = useState("00");
  const [secondsUntil, setSecondsUntil] = useState("00");

  let daysUntill;
  useEffect(() => {
    const timer = setInterval(() => {
      const days = new Date().getTime();
      const birthday = new Date(2022, 3, 29).getTime();
      daysUntill = (birthday - days) / (1000 * 60 * 60 * 24);
      setRemainingDays(Math.floor(daysUntill.toFixed(0)));
      setHoursUntil(((daysUntill * 24) % 24).toFixed(0));
      setMinutesUntil(Math.floor((daysUntill * (24 * 60)) % 60));
      setSecondsUntil(Math.floor((daysUntill * (24 * 60 * 60)) % 60));
    }, 1000);
    // const startTimer = () => {
    //   const timer = setInterval(() => {
    //     const days = new Date().getTime();
    //     const birthday = new Date(2022, 4, 30).getTime();
    //     daysUntill = birthday - days;
    //     setRemainingDays(Math.floor(daysUntill / (1000 * 60 * 60 * 24)));
    //     setHoursUntil((daysUntill % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    //     setMinutesUntil(
    //       Math.floor((daysUntill % (1000 * 60 * 60)) / (1000 * 60))
    //     );
    //     setSecondsUntil((daysUntill % (1000 * 60)) / 1000);
    //     return () => clearInterval(timer);
    //   }, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div>
      <h3>{remainingDays}</h3>
      <h3>{hoursUntill}</h3>
      <h3>{minutesUntil}</h3>
      <h3>{secondsUntil}</h3>
      <h2>{`${remainingDays}: ${hoursUntill}: ${minutesUntil}: ${secondsUntil}`}</h2>
    </div>
  );
}

export default App;
