import { useEffect, useState } from "react";

function App() {
  // const remainingDays = daysUntill.toFixed(0);
  // const hoursUntill = ((daysUntill * 24) % 24).toFixed(0);
  // const minutesUntill = ((daysUntill * (24 * 60)) % 60).toFixed(0);
  // let secondsUntill = ((daysUntill * (24 * 60 * 60)) % 60).toFixed(0);

  const [remainingDays, setRemainingDays] = useState("");
  const [hoursUntill, setHoursUntil] = useState("");
  const [minutesUntil, setMinutesUntil] = useState("");
  const [secondsUntil, setSecondsUntil] = useState("");

  const [controller, setController] = useState(null);
  const days = new Date().getTime();
  const birthday = new Date("April 28, 2022 18:35:50").getTime();

  let daysUntill;
  useEffect(() => {
    const timer = setInterval(() => {
      daysUntill = (birthday - days) / (1000 * 60 * 60 * 24);
      if (birthday - days < 0) {
        setController(true);
        return;
      }
      setRemainingDays(Math.floor(daysUntill.toFixed(0)));
      setHoursUntil(((daysUntill * 24) % 24).toFixed(0));
      setMinutesUntil(Math.floor((daysUntill * (24 * 60)) % 60));
      setSecondsUntil(Math.floor((daysUntill * (24 * 60 * 60)) % 60));
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div>
      {!controller && (
        <h2>{`${remainingDays}: ${hoursUntill}: ${minutesUntil}: ${secondsUntil}`}</h2>
      )}
      {controller && <h2>Happy Birthday Sarah</h2>}
    </div>
  );
}

export default App;
